// Importações
import ProjectForm from '../project/ProjectForm'
import styles from './NewProject.module.css'
import {useHistory} from 'react-router-dom'

function NewProject() {

    // Esse hook vai permitir fazer redirects nas páginas - o usuário será redirecionado
    const history = useHistory()

    function createPost(project) {
        // Passando um projeto pro createPost e inserir um projeto no sistema
        // initialize cost and services
        project.cost = 0
        project.services = []

        fetch("http:/localhost:5000/projects", {
            // Método para fazer envio de form
            method: "POST",
            headers: {
                // Comunicação com o json
                'Content-type': "application/json",
            }
        })
        .then(
            // Recebemos uma resposta e transforma ela em json
            ((resp) => resp.json())
        // Esse then() recebe os dados
        .then((data) => {
            console.log(data)
        })
        ) // Possivel erro do servidor
        .catch(err => console.log(err))
    }

    return (
        <div className={styles.newproject_container}>
            <h1>Criar um novo projeto</h1>
            <p>Crie um projeto e gerencie seus serviços!</p>
            <ProjectForm btnText="Criar projeto" />
        </div>
    )
}

export default NewProject
