// Importações
import styles from './Message.module.css'
import {  useState, useEffect  } from 'react'
import { FaCircleXmark, FaCheck } from 'react-icons/fa6'

function Message({type, msg}) {

    const [visible, setVisible] = useState(false)
    // Alterando a visibilidade da mensagem, dependendo de uma condição
    const [iconeV, setIconV] = useState(false)
    const [iconeF, setIconF] = useState(false)

    useEffect(() => {

        if(!msg) {
            setVisible(false)
            return
        }

        setVisible(true)

        const timer = setTimeout(() => {
            setVisible(false)
        }, 3000)

        return

    }, [msg])

    return (
        <>
            {visible && iconeV(
                <div className={`${styles.message} ${styles[type]}`}><FaCheck/>{msg}</div>
                // Classes dinâmicas, uma fixa e outra vêm das propriedades
            )},
            {visible && iconeF(
                <div className={`${styles.message} ${styles[type]}`}><FaCircleXmark/>{msg}</div>
                // Classes dinâmicas, uma fixa e outra vêm das propriedades
            )}
        </>
    )
}

export default Message
