    const [visible, setVisible] = useState(false)
    // Alterando a visibilidade da mensagem, dependendo de uma condição
    const [iconeV, setIconV] = useState(false)

    return (
        <>
            {visible && (
                {iconeV}
            )}
        </>
    )
}

export default Message
