// Option - Caixa de seleção
// disabled - Desabilita a caixa de seleção

// Importações
import styles from './ProjectForm.module.css'
import Input from '../form/Input'
import Select from '../form/Select'
import LinkButton from '../layout/LinkButton'
import {useState} from 'react'

function ProjectForm({btnText}) {
    // Inicializando o valores deles como vazio - array vazio
    const [categories, setCategories] = useState([])

    // Request de get no endereço do banco de dados
    // fetch - API do JavaScript
    fetch("http://localhost:5000/categories", {
        method: "GET", // Método de pegar - GET
        headers: {
            // Tipo da comunicação - Rece
            'Content-Type': 'application/json'
        }
    })
    
    return (
        <form className={styles.form}>
            <Input 
            type="text"
            text="Nome do projeto"
            name="name"
            placeholder="Insira o nome do projeto"
            />
            <Input type="number" text="Orçamento do projeto" name="budget" placeholder="Insira o orçamento do projeto"/>
            <Select name="category_id" text="Selecione a categoria"/>

            <LinkButton type="submit" to="/" text={btnText}/>
        </form>
    )
}

export default ProjectForm
