export const request = (path) => {
    return fetch(`http://localhost:5000${path}`, {
        // Parametros
        method: 'GET',
        headers: {
            'Content-Type': 'application/json',
        },
    }).then(data => {
        return(data)
    }).catch((err) => console.log(err))
}
