// Option - Caixa de seleção
// disabled - Desabilita a caixa de seleção

// Importações
import styles from './ProjectForm.module.css'
import Input from '../form/Input'
import Select from '../form/Select'
import LinkButton from '../layout/LinkButton'
import { useEffect, useState } from 'react'

// projectData - Dados do projeto => envio de projeto pra edição => passa pela página pai

function ProjectForm({ handleSubmit, btnText, projectData  }) {
    // Inicializando o valores deles como vazio - array vazio
    const [categories, setCategories] = useState([])
    // Recebo o projectData do componente pai
    const [project, setProject] = useState(projectData || {})

    useEffect(() => {
        // Request de get no endereço do banco de dados
        // fetch - API do JavaScript
        fetch("http://localhost:5000/categories", {
            method: "GET", // Método de pegar - GET
            headers: {
                // Tipo da comunicação - Recebendo json
                'Content-Type': 'application/json'
            }
        })
            // Métodos
            // then() - Processos para fazer com a API
            .then((resp) => resp.json()) // O que receber de dado, vai virar json
            // Aproveitando o dados  que foram pegos da resposta
            .then((data) => {
                setCategories(data)
                // Usando os dados do json e coloquei no hook de setCategories
            })
            .catch(err => console.log(err))
    }, [])

    const submit = (e) => {
        e.preventDefault()
        handleSubmit(project)
    }

    function handleChange(e) {
        // Pegar todo o dado do project, um state, e.target.name que é o nome do input
        // Independende do input preenchido 
        setProject({ ...project, [e.target.name]: e.target.value})
    }

    function handleCategory(e) {
        // Pegar todo o dado do project, um state, e.target.name que é o nome do input
        // Independende do input preenchido 
        setProject({ ...project, category: {
            id: e.target.value,
            name:e.target.options
        }})

    }

    return (
        <form onSubmit={submit} className={styles.form}>
            <Input
                type="text"
                text="Nome do projeto"
                name="name"
                placeholder="Insira o nome do projeto"
                handleOnChange={handleChange}
            />
            <Input
                type="number"
                text="Orçamento do projeto"
                name="budget"
                placeholder="Insira o orçamento do projeto"
                handleOnChange={handleChange}
            />
            <Select
                name="category_id"
                text="Selecione a categoria"
                options={categories}
            />

            <LinkButton type="submit" to="/" text={btnText} />
        </form>
    )
}

export default ProjectForm
